unordered_map<int, unordered_set<int>> preMap;
    vector<bool> matched;

void dfs(int cur) {
        for(int p : preMap[cur]) {
            if(!matched[p]) {
                dfs(p);
                matched[p] = true;
            }
            for(int c : preMap[p]) {
                preMap[cur].insert(c);
            }
        }
    }

    vector<bool> checkIfPrerequisite(int numCourses, vector<vector<int>>& prerequisites, vector<vector<int>>& queries) {
        vector<bool> ans;
        matched.resize(numCourses, false);

        for(int i = 0; i < prerequisites.size(); ++i) {
            preMap[prerequisites[i][1]].insert(prerequisites[i][0]);
        }

        for(int i = 0; i < numCourses; ++i) {
            dfs(i);
        }

        for(auto pairs : queries) {
            ans.push_back(preMap[pairs[1]].find(pairs[0]) != preMap[pairs[1]].end());
        }

        return ans;
    }

    
